import React, { useState } from 'react';

export const TaskMenu = () => {

    const [showTaskMenu, setShowTaskMenu] = React.useState(true)

    const toggleTaskMenu = () => showTaskMenu ? setShowTaskMenu(false) : setShowTaskMenu(true)

    const overlayed = {
        position: 'fixed',
        zIndex: '1'
    };
    return ( 
        showTaskMenu ?
            <div className='overlay' style={overlayed}>
                <div className='task-menu' >
                    <div className='task-menu-header' >
                        <h2>Task {showTaskMenu}</h2>
                        <button className='exit-task-menu' onClick={toggleTaskMenu}>Hello</button>  
                    </div>
                    <div className='task-menu-body'>
                    <div className='task-menu-body-left-col'>a</div>
                    <div className='task-menu-body-right-col'>b</div>
                    {/* <Form label='Title'/>
                    <Form label='Description'/>
                    <Form label='Estimation'/> */}
                    </div>
                </div>

            </div>
        : null 
    );
};

export function Form(props) {
  const [name, setName] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert('The name you entered was: ');
  }

  const spacing = {
    padding: 10,
    margin: 10
    };

  return (
    <form onSubmit={handleSubmit}>
      <label style={spacing} >{props.label}
        <input 
          type="text" 
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={spacing}
        />
      </label>
      {/* <input type="submit" /> */}

    </form>
  )
}
