import React, { useState } from 'react';

export const TaskMenu = () => {
    const overlayed = {
        position: 'fixed',
        zIndex: '1'
    };
    return ( 
    <div className='overlay' style={overlayed}>
        <div className='task-menu' style={overlayed}>
            <h2 style={overlayed}>Task</h2>
        </div>
    </div>
    );
};

