import React, { useState } from 'react';

export const TaskMenu = () => {

    const [showTaskMenu, setShowTaskMenu] = React.useState(true)

    const toggleTaskMenu = () => showTaskMenu ? setShowTaskMenu(false) : setShowTaskMenu(true)

    const overlayed = {
        position: 'fixed',
        zIndex: '1'
    };
    return ( 
        showTaskMenu ?
            <div className='overlay' style={overlayed}>
                <div className='task-menu' style={overlayed}>
                    <div className='task-menu-header' style={overlayed}>
                        <h2 style={overlayed}>Task {showTaskMenu}</h2>
                        <button style={overlayed} className='exit-task-menu' onClick={toggleTaskMenu}>Hello</button>  
                    </div>
                </div>
            </div>
        : null 
    );
};

export function Form() {
  const [name, setName] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert('The name you entered was: ${name}');
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>Enter your name:
        <input 
          type="text" 
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <input type="submit" />
    </form>
  )
}
